import React,{useState} from "react"
import {Navigate} from "react-router-dom"
import axios from "axios"

export default function Login() {
  const [id,setId] = useState(''); 
  const [passwd,setPasswd] = useState('');
  const Login = () => {
    axios.post('http://localhost/php/dongwonmall/login.php', {'id': id, 'passwd': passwd}).then((response) => {
      if(response.data.res){
        alert('yes');
        <Navigate to="/" replace={true} />
      }
      else{
        <Navigate to="/login/failed" replace={true} />
      }
    })
  }


  return (
    <div className="h-screen bg-gray-200 flex items-center justify-center">
      <div className="bg-blue-300 px-10 h-128 rounded-l-lg flex items-center justify-center">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-12 w-12"
          fill="none"
          viewBox="0 0 24 24"
          stroke="#2298c7"
          strokeWidth={2}
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1"
          />
        </svg>
      </div>
      <div className="bg-blue-200 h-128 px-64 rounded-r-lg text-center">
        <img className="m-auto my-16 h-20 w-20" src="https://cdn-icons-png.flaticon.com/512/660/660611.png" alt=""></img>
        <div className="flex">
          <div>
            <input onChange={ (e) => setId(e.target.value) } onKeyDown={(e) => {if(e.key === 'Enter'){Login()}}} className="block px-4 py-2 rounded-md border-2 border-sky-300" type="text" placeholder="IDを入力してください" />
            <input onChange={ (e) => setPasswd(e.target.value) } onKeyDown={(e) => {if(e.key === 'Enter'){Login()}}} className="block mt-4 px-4 py-2 rounded-md border-2 border-sky-300" type="password"  placeholder="Password" />
          </div>
        <button onClick={ () => Login() } className="bg-sky-400 rounded-lg p-4 relative left-8">ログイン</button>
        </div>
      </div>

    </div>
  );
}
